precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}
uniform CCGlobal {
  highp   vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
  mediump vec4 cc_nativeSize;
  highp   mat4 cc_matView;
  mediump mat4 cc_matViewInv;
  mediump mat4 cc_matProj;
  mediump mat4 cc_matProjInv;
  mediump mat4 cc_matViewProj;
  mediump mat4 cc_matViewProjInv;
  mediump vec4 cc_cameraPos;
};
in vec4 v_color;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
#endif
float circle(in vec2 uv, in float radius){
  return 1.0 - smoothstep(radius-0.01*radius, radius+0.01*radius, length(uv));
}
const float PI = 3.141592653;
void main () {
  vec4 o = vec4(0, 0, 0, 0);
  #if USE_TEXTURE
    vec2 uv = v_uv0 - 0.5;
    float R = 0.35;
    float radius = 0.05;
    vec2 pos = vec2(0.0);
    float rotation = 0.0;
    const float count = 12.0;
    float time = cc_time.x;
    for(float i = 0.0; i<count; i++){
      rotation = 2.0*PI*i/count + time*2.0*PI/4.0;
      pos = vec2(R*cos(rotation), R*sin(rotation));
      o.a += circle(uv-pos, radius * (sin(rotation*0.45)+1.0)/3.0 + radius/3.0);
      o.r += o.a * (sin(i/count + time + PI*6.0/3.0)+ 1.0)/2.0;
      o.g += o.a * (sin(i/count + time + PI*4.0/3.0)+1.0)/2.0;
      o.b += o.a * (sin(i/count + time + PI*2.0/3.0)+1.0)/2.0;
    }
  #endif
  o *= v_color;
  ALPHA_TEST(o);
  gl_FragColor = o;
}

